var BufferStringToSymbol = (function() {
	var returnObject = {};
	Object.keys(SymbolToBufferString).forEach(function(symbol){
		returnObject[SymbolToBufferString[symbol]] = symbol;
	});
	return returnObject;
})();



module.exports = {
	SymbolToBufferString : SymbolToBufferString,
	BufferStringToSymbol : BufferStringToSymbol,
	getSymbol :	function(buffer){
					var bs = JSON.stringify(buffer.toJSON());
					return BufferStringToSymbol[bs];
				},
	getBuffer : function(symbol){
					var array = JSON.parse(SymbolToBufferString[symbol]);
					var buf = new Buffer(array);
					return buf;
				},
}
